dice_nums = ["1", "2", "3", "4", "5", "6"]
welldone = ["GG", "Good work!", "WOW!", "WellDone :)"]
def dice():
    clear_screen()
    print("~~~Dice~~~\n"
          "Version{}".format(data.DICE_VER))
    wait()
    print("You rolled a {} {}".format(random.choice(dice_nums), random.choice(welldone)))
    wait()
    menu()
fortunes = ["You will have a good day!", "You wont have a good day!", "You will play outside today", "You will win money! $$$", "you will keep a toy!"]
def fortune():
    clear_screen()
    print("~~~Fortune~~~\n"
          "Version: 1.1")
    wait()
    print("{}".format(random.choice(fortunes)))
menu_effects = ["+", "=", "-"]
goodbye = ["see you later space cowboy!", "Goodbye!", "*.*", "See you next time!", ";)", "Exited sucssesfully", "Erm... bye :(", "Good game!"]
def menu():
    clear_screen()
    print("================\n"
          "~~~Multi~Game~~~\n"
          "================\n")
    print("re. Reload     {}".format(random.choice(menu_effects)))
    print("{} 1. Space Game".format(random.choice(menu_effects)))
    print("2. Heads or Tails  {}".format(random.choice(menu_effects)))
    print("{} 3. Fidget spinner".format(random.choice(menu_effects)))
    print("4. Spam (warning!) {}".format(random.choice(menu_effects)))
    print("{} 5. Dice".format(random.choice(menu_effects)))
    print("{} i. information".format(random.choice(menu_effects)))
    print("0. Quit {}".format(random.choice(menu_effects)))
    choice = user_choice()
    if choice == "re":
        menu()
    if choice == "1":
        space_count()
    if choice == "2":
        heads_tails()
    if choice == "3":
        spinner()
    if choice == "4":
        spam()
    if choice == "5":
        dice()
    if choice == "i":
        info()
    if choice == "0":
        wait()
        clear_screen()
        print("{}".format(random.choice(goodbye)))

menu = menu()
print(menu)

